targets.expr:
sum by (pod, $clusterNameLabel, response_code_class) (
    rate(
        envoy_cluster_upstream_rq{
            namespace=~"$Namespace",pod=~"$pod", $clusterNameLabel="$cluster"
        }
    [$__rate_interval])
)

sum by (pod, $clusterNameLabel, response_code) (
    rate(
        envoy_cluster_upstream_rq{
            namespace=~"$Namespace",pod=~"$pod", $clusterNameLabel="$cluster"
        }
    [$__rate_interval])
)

sum by (pod, $clusterNameLabel, response_code) (
    rate(
        envoy_cluster_upstream_rq{
            namespace=~"$Namespace",pod=~"$pod", $clusterNameLabel="$cluster",
            response_code=~"5.."
        }
    [$__rate_interval])
)

histogram_quantile(
  0.9,
  sum by (pod, $clusterNameLabel, le) (
    rate(
      envoy_cluster_upstream_cx_connect_ms_bucket{
        namespace=~"$Namespace",pod=~"$pod", $clusterNameLabel="$cluster"
      }
    [$__rate_interval])
  )
)

histogram_quantile(
  0.9,
  sum by (pod, $clusterNameLabel, le) (
    rate(
      envoy_cluster_upstream_cx_length_ms_bucket{
        namespace=~"$Namespace",pod=~"$pod", $clusterNameLabel="$cluster"
      }
    [$__rate_interval])
  )
)
